const todosUrl = "http://127.0.0.1:3000/todos";

export function patchTodo(updatedTodo){
    fetch(todosUrl + "/" + updatedTodo.id, {
        method: "PATCH",
        headers: {
          "Content-Type": "application/json"
        },
        body: JSON.stringify({todo: updatedTodo})
      })
}
